stringfield.script:
function onGainFocus()
	setColor("#FF990099");
end

function onLoseFocus()
	setColor(nil);
end

numberfield.script:
function onGainFocus()
	setColor("#FF990099");
end

function onLoseFocus()
	setColor(nil);
end
function onHover(state)
	if state then
		setColor("#FF990099");
		setFrame("sheetfocus", 5, 5, 5, 5);
	else
		if hasFocus() == false then
			setColor(nil);
			setFrame("modifier", 5, 5, 5, 5);
		end
	end
end

function onValueChanged()
	if getValue() < 0 then
		setValue(0);
	elseif getValue() > 70 then
		setValue(70);
	end
end
