checkbox.script:
function onHover(state)
	if state then
		window.setFrame("rowshade");
	else
		window.setFrame(nil);
	end
end

textlistitemvalue.script:
function onDoubleClick()
	win = Interface.openWindow("defensefull",window.getDatabaseNode());
end
function onLoseFocus()
	super.onLoseFocus();
	window.windowlist.applySort();
end

modifiernumber.script:
function onHover(state)
	if state then
		setColor("#FF228822");
		window.setFrame("rowshade");
	else
		if hasFocus() == false then
			setColor(nil);
			window.setFrame(nil);
		end
	end
end

