	setColor(nil);
end

function onGainFocus()
	setColor("#FF990099");
end

function onLoseFocus()
	setColor(nil);
end
function onHover(state)
	if state then
		setColor("#FF990099");
		setFrame("sheetfocus", 3, 3, 3, 3);
	else
		if hasFocus() == false then
			setColor(nil);
			setFrame("modifier", 3, 3, 3, 3);
		end
	end
end

